sections.css:
h1,
h2,
h3,
h4,
h5 {
    font-family: $base__font-family--heading;
    font-weight: $base__font-weight--heading;
    line-height: $base__line-height--heading;
    margin-bottom: $spacing--small;
}

h1,
.h1 {
  $font-size: $font-size--big;
}

h2,
.h2 {
  $font-size: $font-size--large;
}

h3,
.h3 {
  $font-size: $font-size--xlarge;
}

.btn,
button,
input[type="button"],
input[type="reset"],
input[type="submit"] {
    font-family: $font-family--secondary;
    font-size: $font-size--small;
    font-weight: $font-weight--medium;
    letter-spacing: 1px;
    line-height: $base__line-height;
    display: inline-block;
    padding: ($spacing--small / 2) $spacing;
    cursor: pointer;
    text-align: center;
    white-space: nowrap;
    text-decoration: none;
    border-width: $button__border--width;
    border-style: solid;
    border-radius: $border__radius;
    transition: background $transition__time ease-out,
                color $transition__time ease-out;

    &:hover,
    &:focus {
        background-color: transparent;
    }

    &:disabled {
        cursor: not-allowed;
        color: darken($color--grey, 20%);
        border-color: $color--grey;
        background: $color--grey;

        &:hover {
            color: darken($color--grey, 20%);
            border-color: $color--grey;
            background: $color--grey;
        }
    }
}

.btn {
    @include button-colors($ui-colors);
}

.btn--outline {
    @include button-colors--outline($ui-colors);
}

.btn--text {
    color: $color--blue;
    border: none;
    background: none;

    &:hover,
    &:focus {
        color: lighten($color--blue, 10%);
    }
}

.btn--theme1 {
    border-color: $color__fg2;
    color: $color__fg2--bold;

    &:hover {
        background: $color__fg2--light;
    }
}

.btn--theme1--solid {
    border-color: $color__fg2--bold;
    color: $color__fg1--bold;
    background: $color__fg2--bold;

    &:hover {
        background: $color__fg2--light;
        color: $color__fg2--bold;
    }
}

.btn--theme2 {
    border-color: $color__fg1;
    color: $color__fg1--bold;

    &:hover {
        background: $color__fg1--light;
    }
}

.btn--theme2--solid {
    border-color: $color__fg1--bold;
    background: $color__fg1--bold;
    color: $color__fg2--bold;

    &:hover {
        background: $color__fg2;
        color: $color__fg1--bold;
    }
}

ul {
    @extend %text;
    margin-top: 0;
    padding-left: 0;
    list-style: circle inside;

    li {
        margin-bottom: $spacing--small;
    }

    ul,
    dl,
    ol {
        font-size: 90%;
        margin: $spacing--small 0 $spacing--small $spacing;
    }
}

ol {
    @extend %text;
    margin-top: 0;
    padding-left: 0;
    list-style: decimal inside;

    li {
        margin-bottom: $spacing--small;
    }

    ul,
    dl,
    ol {
        font-size: 90%;
        margin: $spacing--small 0 $spacing--small $spacing;
    }
}

dl {
    @extend %text;
    margin-top: 0;
    padding-left: 0;

    dt,
    dd {
        margin-bottom: $spacing--small;
    }

    dt {
        font-weight: $font-weight--bold;
    }

    dd {
        padding-left: $spacing;
    }

    ul,
    dl,
    ol {
        font-size: 90%;
        margin: $spacing--small 0 $spacing--small $spacing;
    }
}

.list--horiz {
    margin: 0;
    padding: 0;
    list-style: none;
}

.list--horiz__item {
    display: inline-block;
    margin-bottom: 0;
    margin-left: $spacing--small;

    &:first-child {
        margin-left: 0;
    }
}

input[type="email"],
input[type="number"],
input[type="password"],
input[type="search"],
input[type="tel"],
input[type="text"],
input[type="url"],
textarea,
select {
    @extend %text;
    width: 100%;
    padding: $spacing--small;
    border: $forms__border--width $forms__border--color $forms__border--style;
    border-radius: $border__radius;
    background-color: transparent;
    box-shadow: none;
    appearance: none; // Removes awkward default styles on some inputs for iOS

    &:focus {
        border-color: $forms__border--color--focus;
        outline: 0;
    }
}

label {
    @extend %text;
}

.field-container {
    margin-top: $spacing;

    &:first-child {
        margin-top: 0;
    }
}

table {
    @extend %text;
    width: 100%;

    th,
    td {
        text-align: left;
        border-bottom: $table__border--width $table__border--style $table__border--color;
        padding: $spacing--small;

        &:first-child {
            padding-left: 0;
        }

        &:last-child {
            padding-right: 0;
        }
    }
}

a,
a:visited {
    font-family: $button__font-stack;
    text-decoration: none;
    color: $color--blue;
    &:hover,
    &:focus {
        color: darken($color--blue, 20%);
    }
}

.logo__link {
    display: block;
    max-width: $spacing * 3;
    height: 100%;
    max-height: $spacing * 3;
    fill: $color--blue;
    stroke: $color--blue;

    > svg {
        max-width: $spacing * 3;
        max-height: $spacing * 3;
    }
}

