sections.css:
h1,
h2,
h3,
h4,
h5 {
    font-family: $base__font-family--heading;
    font-weight: $base__font-weight--heading;
    line-height: $base__line-height--heading;
    margin-bottom: $spacing--small;
}

h1,
.h1 {
  $font-size: $font-size--big;
}

h2,
.h2 {
  $font-size: $font-size--large;
}

h3,
.h3 {
  $font-size: $font-size--xlarge;
}

.btn,
a.btn,
button,
input[type="button"],
input[type="reset"],
input[type="submit"] {
    font-family: $btn__font-stack;
    font-size: $font-size--small;
    font-weight: $font-weight--medium;
    letter-spacing: 1px;
    line-height: $base__line-height;
    display: inline-block;
    padding: ($spacing--small / 2) $spacing;
    cursor: pointer;
    text-align: center;
    white-space: nowrap;
    text-decoration: none;
    border: $btn__border-width solid $btn__border-color;
    background: $btn__color;
    color: $btn__font-color;
    border-radius: $border__radius;
    transition: background $transition__time ease-out,
    color $transition__time ease-out;

    &:hover,
    &:focus {
        background-color: $btn__color--hover;
        border-color: $btn__border-color--hover;
        color: $btn__font-color--hover;
    }

    &:disabled {
        cursor: not-allowed;
        color: darken($color--grey, 20%);
        border-color: $color--grey;
        background: $color--grey;

        &:hover {
            color: darken($color--grey, 20%);
            border-color: $color--grey;
            background: $color--grey;
        }
    }
}

.btn {
    @include button-colors($ui-colors);
}

.btn--outline {
    @include button-colors--outline($ui-colors);
}

.btn--text {
    color: $color--blue;
    border: none;
    background: none;

    &:hover,
    &:focus {
        color: lighten($color--blue, 10%);
    }
}

ul {
    @extend %text;
    margin-top: 0;
    padding-left: 0;
    list-style: circle inside;

    li {
        margin-bottom: $spacing--small;
    }

    ul,
    dl,
    ol {
        font-size: 90%;
        margin: $spacing--small 0 $spacing--small $spacing;
    }
}

ol {
    @extend %text;
    margin-top: 0;
    padding-left: 0;
    list-style: decimal inside;

    li {
        margin-bottom: $spacing--small;
    }

    ul,
    dl,
    ol {
        font-size: 90%;
        margin: $spacing--small 0 $spacing--small $spacing;
    }
}

dl {
    @extend %text;
    margin-top: 0;
    padding-left: 0;

    dt,
    dd {
        margin-bottom: $spacing--small;
    }

    dt {
        font-weight: $font-weight--bold;
    }

    dd {
        padding-left: $spacing;
    }

    ul,
    dl,
    ol {
        font-size: 90%;
        margin: $spacing--small 0 $spacing--small $spacing;
    }
}

.list--horiz {
    margin: 0;
    padding: 0;
    list-style: none;
}

.list--horiz__item {
    display: inline-block;
    margin-bottom: 0;
    margin-left: $spacing--small;

    &:first-child {
        margin-left: 0;
    }
}

input[type="email"],
input[type="number"],
input[type="password"],
input[type="search"],
input[type="tel"],
input[type="text"],
input[type="url"],
textarea,
select {
    @extend %text;
    width: 100%;
    padding: $spacing--small;
    border: $forms__border--width $forms__border--color $forms__border--style;
    border-radius: $border__radius;
    background-color: transparent;
    box-shadow: none;
    appearance: none; // Removes awkward default styles on some inputs for iOS

    &:focus {
        border-color: $forms__border--color--focus;
        outline: 0;
    }
}

label {
    @extend %text;
}

.field-container {
    margin-top: $spacing;

    &:first-child {
        margin-top: 0;
    }
}

table {
    @extend %text;
    width: 100%;

    th,
    td {
        text-align: left;
        border-bottom: $table__border--width $table__border--style $table__border--color;
        padding: $spacing--small;

        &:first-child {
            padding-left: 0;
        }

        &:last-child {
            padding-right: 0;
        }
    }
}

a,
a:visited {
    font-family: $btn__font-stack;
    text-decoration: none;
    color: $color--blue;
    &:hover,
    &:focus {
        color: darken($color--blue, 20%);
    }
}

.logo__link {
    display: block;
    max-width: $spacing * 3;
    height: 100%;
    max-height: $spacing * 3;
    fill: $color--blue;
    stroke: $color--blue;

    > svg {
        max-width: $spacing * 3;
        max-height: $spacing * 3;
    }
}

// Reset UL
.block-grid {
    margin: 0;
    padding: 0;
    list-style: none;
}

@include breakpoint($medium) {
    .block-grid {
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
    }

    .block-grid__item {
        flex: 1 0 50%;
    }
}

@include breakpoint($large) {
    .block-grid__item {
        flex: 1 0 30%;
    }
}

.main-nav__list {
    padding: 0;
    margin: 0;
    list-style: none;

    @include breakpoint(max-width $xlarge) {
        display: flex;
        flex-wrap: wrap;
        justify-content: center;
    }
}

.main-nav__item {
    @include breakpoint($large) {
        margin-bottom: 0;
    }
    @include breakpoint(max-width $large) {
        margin-bottom: 0;
    }
}

.main-nav__link {
    display: block;
    padding: $spacing--small;

    @include breakpoint($xlarge) {
        text-align: right;
    }
}

svg {
    width: 100%;
}

// Layout

.section-intro {
    background-image: url("../img/intro-bg.svg");
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: top right;
    background-size: 100% 100%;
    font-size: 150%;

    @include breakpoint(max-width $xlarge) {
        padding-top: $spacing * 4;
        padding-bottom: $spacing * 4;
    }

    .section__inner {
        padding-left: $spacing;
        padding-right: $spacing;
    }
}


@include breakpoint($xlarge) {

    .site-wrap {
        display: flex;
    }

    .section-intro {
        display: flex;
        height: 100vh;

        .section__inner {
            align-self: center;
            padding-left: $spacing * 2;
        }
    }

    .main-content {
        flex: 1 1 0;
        overflow-x: hidden;
        overflow-y: scroll;
        margin-left: $spacing * 10;

        .section__inner {
            max-width: ($spacing * 40);
        }
    }

    .sidebar {
        cursor: default;
        height: 100vh;
        left: 0;
        overflow-x: hidden;
        overflow-y: auto;
        position: fixed;
        top: 0;
        width: $spacing * 10;
        z-index: 10000;
        padding: $spacing;
    }

    .main-nav {
        min-height: 100%;
        width: 100%;
        display: flex;
    }

    .main-nav__list {
        align-self: center;
        width: 100%;
    }

}

@include breakpoint(max-width $xlarge) {
    .sidebar {
        position: fixed;
        width: 100%;
    }
}


.section-what {
    ul {
        padding: 0;
        list-style: none;

        li {
            margin-bottom: 0;
            padding: $spacing--small;

            @include breakpoint(max-width $xlarge) {
                .icn-wrap {
                    flex: 0 0 $spacing;
                    margin-right: $spacing--small;
                    display: flex;

                    svg {
                        margin-top: $spacing * .25;
                        width: $spacing;
                        height: $spacing;
                    }
                }
            }
        }
    }
}

.main-nav__link {
    position: relative;
    &::before {
        content: '';
        position: absolute;
        left: 0;
        bottom: 0;
        width: 100%;
        background: $color--theme6;
        height: 4px;
        transform: scale3d(0, 1, 1);
    	transition: transform 0.1s;
    }
}

.is-current {
    .main-nav__link {
        &::before {
            transform: scale3d(1, 1, 1);
        	transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
        	transition-duration: 0.3s;
        }
    }
}

